dt-example.js:
$(document).ready(function() {
	window.table = $('#example').DataTable( {
		dom: 'Rlfrtip',
		scrollX: true,
		scrollCollapse: true,
		columnDefs: [
			{ orderable: false, targets: 0 },
			{ orderable: false, targets: -1 }
		],
		ordering: [[ 1, 'asc' ]],
		colReorder: {
			fixedColumnsLeft: 1,
			fixedColumnsRight: 1
		}
	} );

	window.fc = new $.fn.dataTable.FixedColumns( table, {
		leftColumns: 1,
		rightColumns: 1
	} );
} );
