dt-example.js:
$(document).ready(function() {
	$('#example').dataTable( {
		dom: 'Rlfrtip',
		processing: true,
		serverSide: true,
		ajax: "../../../examples/server_side/scripts/objects.php",
		columns: [
			{ data: "first_name" },
			{ data: "last_name" },
			{ data: "position" },
			{ data: "office" },
			{ data: "start_date" },
			{ data: "salary" }
		]
	} );
} );
